import React, { useEffect, useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { CKEditor, CKEditorContext } from '@ckeditor/ckeditor5-react';

import Context from '@ckeditor/ckeditor5-core/src/context';
import ClassicEditor from '@ckeditor/ckeditor5-editor-classic/src/classiceditor';
import Bold from '@ckeditor/ckeditor5-basic-styles/src/bold';
import Italic from '@ckeditor/ckeditor5-basic-styles/src/italic';
import Essentials from '@ckeditor/ckeditor5-essentials/src/essentials';
import Paragraph from '@ckeditor/ckeditor5-paragraph/src/paragraph';

function AskQuestionScreen(props) {

    const question = useSelector((state) => state.questionSave);
    const { quest, questloading, questerror } = question;
    const [title, setTitle] = useState('');
    const [description, setDescription] = useState('');
    const [tags, setTags] = useState('');

    const submitHandler = (e) => {
        e.preventDefault();
        // dispatch(signin(email, password));

    }



    return (
        <form  onSubmit={submitHandler} className="ask-question">
        <div className="form-group"><small className="form-text text-muted question-label" >Question Title*</small><label style={{color: 'rgb(245,30,16)'}}>Be specific and imagine youâ€™re asking a question to another person</label>
            <input className="form-control form-control-lg" onChange={(e) => setTitle(e.target.value)} type="text" placeholder="e.g. Is there an R function for finding the index of an element in a vector?" style={{fontSize: '12px', fontFamily: 'Montserrat, sans-serif'}} required/>
        </div>
        <div className="form-group"><small className="form-text text-muted question-label" >Question Body*</small><label style={{color: 'rgb(245,30,16)'}}>Include all the information someone would need to answer your question</label>
            <textarea className="form-control form-control-lg" onChange={(e) => setDescription(e.target.value)} required></textarea>
        </div>
        <div className="form-group"><small className="form-text text-muted" style={{fontSize: '19px',fontFamily: 'Lora, serif'}}>Question Tags*</small><label style={{color: 'rgb(245,30,16)'}}>Add up to 5 tags to describe what your question is about</label>
            <input className="form-control" onChange={(e) => setTags(e.target.value)} type="text" placeholder="e.g. net,php,json,python" style={{fontSize: '12px', fontFamily: 'Montserrat, sans-serif'}} required/>
        </div>
        <div className="form-group">
            {questloading && <div>Loading...</div>}
            {questerror && <div>{questerror}</div>}
        </div>
        <div className="form-group">
            <button className="btn btn-primary btn-block" type="submit">Save</button>
        </div>
    </form>

    );

}

export default AskQuestionScreen;
