import React, { useEffect, useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { Link } from 'react-router-dom';
import { signin } from '../actions/userActions';

function Login(props) {

    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const userSignin = useSelector(state => state.userSignin);
    const { loading, userInfo, error } = userSignin;
    const dispatch = useDispatch();
    //const redirect = props.location.search ? props.location.search.split("=")[1] : '/';
    useEffect(() => {
        if (userInfo) {
            //props.history.push(redirect);
        }
        return () => {
        //
        };
    }, [userInfo]);

    const submitHandler = (e) => {
        e.preventDefault();
        dispatch(signin(email, password));

    }

    return (
    <div>
	    <div role="dialog" tabindex="-1" class="modal fade" id="login">
		<div class="modal-dialog" role="document">
		    <div class="modal-content">
		        <div class="modal-header">
		            <h4>Log-In</h4><button type="button" class="close" data-dismiss="modal" aria-label="Close"><span aria-hidden="true">Ã—</span></button></div>
                    <div class="modal-body">
                        <div class="login-card" style={{marginBottom: '0px', marginTop: '0px'}}><img class="profile-img-card" src="assets/img/avatar_2x.png" />
                            <p class="profile-name-card"></p>
                            <form class="form-signin" onSubmit={submitHandler}>
                                <input type="email" onChange={(e) => setEmail(e.target.value)} class="form-control" id="inputEmail" required placeholder="Email address" autofocus />
                                <input type="password" onChange={(e) => setPassword(e.target.value)} class="form-control" id="inputPassword" required placeholder="Password" />
                                {loading && <div>Loading...</div>}
                                {error && <div>{error}</div>}
                                <button class="btn btn-primary btn-block btn-lg btn-signin" type="submit">Sign in</button>
                            </form>
                        </div>
                    </div>
		        <div class="modal-footer"><Link to='/register'class="btn btn-primary">Sign Up</Link></div>
		    </div>
		</div>
	    </div>
	</div>
    );

}

export default Login;
