import React, { useEffect, useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { saveQuestion} from '../actions/questionActions';
import {Button,Modal} from 'react-bootstrap';
import  { Redirect } from 'react-router-dom'

import QuestionCard from './QuestionCard';
import { listQuestions } from '../actions/questionActions';

function MainScreen(props) {

    const [title, setTitle] = useState('');
    const [description, setDescription] = useState('');
    const [tags, setTags] = useState('');
    const question = useSelector((state) => state.questionSave);
    const { quest, questloading, questerror } = question;
    const userSignin = useSelector((state) => state.userSignin);
    const { userInfo } = userSignin;
    const questionList = useSelector((state) => state.questionList);
    const { questions, loading, error } = questionList;
    const dispatch = useDispatch();
    useEffect(() => {
        dispatch(listQuestions());
        return () => {
        //
        };
    }, []);

    const [show, setShow] = useState(false);

    const handleClose = () => setShow(false);
    const handleShow = () => {

        if(!userInfo){
            <Redirect to='/login'  />
        } else {
            setShow(true);
        }

    }


    const submitHandler = (e) => {
        e.preventDefault();
        dispatch(saveQuestion(userInfo._id, userInfo.userName, title, description,tags.split(',')));

    }

    return (

    <main className="page landing-page">
        <section className="clean-block slider dark">
            <div className="container-fluid" style = {{ height: '330px'}}>
                <div className="carousel slide" data-ride="carousel" id="carousel-1" style= {{height: '370px'}}>
                    <div className="carousel-inner" style={{height: '370px', marginTop: '30px'}}>
                        <div className="carousel-item active"><img className="w-100 d-block" src="assets/img/scenery/image1.jpg" alt="Slide Image"/></div>
                        <div className="carousel-item"><img className="w-100 d-block" src="assets/img/scenery/image4.jpg" alt="Slide Image"/></div>
                        <div className="carousel-item"><img className="w-100 d-block" src="assets/img/scenery/image6.jpg" alt="Slide Image"/></div>
                    </div>
                    <div><a className="carousel-control-prev" href="#carousel-1" role="button" data-slide="prev"><span className="carousel-control-prev-icon"></span><span className="sr-only">Previous</span></a><a className="carousel-control-next" href="#carousel-1" role="button"
                            data-slide="next"><span className="carousel-control-next-icon"></span><span className="sr-only">Next</span></a></div>
                    <ol className="carousel-indicators">
                        <li data-target="#carousel-1" data-slide-to="0" className="active"></li>
                        <li data-target="#carousel-1" data-slide-to="1"></li>
                        <li data-target="#carousel-1" data-slide-to="2"></li>
                    </ol>
                </div>
            </div>
        </section>
        <Modal show={show} onHide={handleClose} backdrop="static" keyboard={false}>
            <Modal.Header closeButton>
                <Modal.Title>Modal title</Modal.Title>
            </Modal.Header>
            <Modal.Body>
            <form onSubmit={submitHandler}>
	            <div className="form-group"><small className="form-text text-muted" style={{fontSize: '19px',fontFamily: 'Lora, serif'}}>Question Title*</small><label style={{color: 'rgb(245,30,16)'}}>Be specific and imagine you’re asking a question to another person</label>
                    <input className="form-control form-control-lg" onChange={(e) => setTitle(e.target.value)} type="text" placeholder="e.g. Is there an R function for finding the index of an element in a vector?" style={{fontSize: '12px', fontFamily: 'Montserrat, sans-serif'}} required/>
                </div>
                <div className="form-group"><small className="form-text text-muted" style={{fontSize: '19px', fontFamily: 'Lora, serif'}}>Question Body*</small><label style={{color: 'rgb(245,30,16)'}}>Include all the information someone would need to answer your question</label>
                    <textarea className="form-control form-control-lg" onChange={(e) => setDescription(e.target.value)}></textarea>
                </div>
                <div className="form-group"><small className="form-text text-muted" style={{fontSize: '19px',fontFamily: 'Lora, serif'}}>Question Tags*</small><label style={{color: 'rgb(245,30,16)'}}>Add up to 5 tags to describe what your question is about</label>
                    <input className="form-control" onChange={(e) => setTags(e.target.value)} type="text" placeholder="e.g. net,php,json,python" style={{fontSize: '12px', fontFamily: 'Montserrat, sans-serif'}} required/>
                </div>
                <div className="form-group">
                    <button className="btn btn-primary btn-block" type="submit">Save</button>
                </div>
            </form>
            </Modal.Body>
            <Modal.Footer>
                <Button variant="secondary" onClick={handleClose}>Close</Button>
            </Modal.Footer>
        </Modal>

        <section style={{fontFamily: 'Montserrat, sans-serif',marginBottom: '30px',marginTop: '10px'}}>
            <div className="container-fluid" id="question">
                <div className="row">
                    <div className="col-lg-2 col-xl-3 offset-xl-0" style={{width: '82px',background: 'rgba(255,255,255,0)',borderRightStyle: 'none'}}>
                        <ul className="list-group text-body" style={{ padding: '20px',fontSize: '16px',fontFamily: 'Lora, serif',paddingTop: '50px'}}>
                            <li className="list-group-item" style={{borderStyle: 'none'}}><i className="fas fa-home" style={{marginRight: '9px'}}></i><span style={{fontFamily: 'Lora, serif'}}>Home</span></li>
                            <li className="list-group-item" style={{borderStyle: 'none'}}><i className="fas fa-tags" style={{marginRight: '9px'}}></i><span>Tags</span></li>
                            <li className="list-group-item" style={{borderStyle: 'none'}}><i className="fas fa-users" style={{marginRight: '9px'}}></i><span>Users</span></li>
                            <li className="list-group-item" style={{borderStyle: 'none'}}><i className="fas fa-book-reader" style={{marginRight: '9px'}}></i><span>New Questions</span></li>
                            <li className="list-group-item" style={{borderStyle: 'none'}}><i className="material-icons" style={{marginRight: '6px',marginTop: '0px'}}>trending_up</i><span>Trending Questions</span></li>
                            <li className="list-group-item" style={{borderStyle: 'none'}}><i className="fas fa-vote-yea" style={{marginRight: '9px'}}></i><span>Voted Questions</span></li>
                            <li className="list-group-item" style={{borderStyle: 'none'}}><i className="fas fa-blog" style={{marginRight: '9px'}}></i><span>Blog</span></li>
                            <li className="list-group-item" style={{borderStyle: 'none'}}><i className="fas fa-hands-helping" style={{marginRight: '9px'}}></i><span>Help</span></li>
                            <li className="list-group-item" style={{borderStyle: 'none'}}><i className="fa fa-group" style={{marginRight: '9px'}}></i><span>About US</span></li>
                        </ul>
                    </div>

                    <div className="col-lg-7 col-xl-7 offset-lg-0" style={{marginBottom: '12px', padding: '0px', borderWidth: '1px', borderRightWidth: '1px', borderRightStyle: 'solid',borderLeftWidth: '1px', borderLeftStyle: 'solid'}}>
                        <div className="container" style={{marginBottom: '0px', borderStyle: 'none', padding: '0px'}}>
                            <nav className="navbar navbar-light navbar-expand-md navigation-clean-search">
                                <div className="container"><a className="navbar-brand" href="#">All Questions</a>
                                    <div className="collapse navbar-collapse" id="navcol-1" style={{width: '485px'}}>

                                        <Button onClick={handleShow} style={{background: 'rgb(58,187,242)', marginLeft: '355px'}}>Ask Question</Button>
                                    </div>
                                </div>
                            </nav>
                            <ul className="nav nav-pills float-right">
                                <li className="nav-item"><a className="nav-link active" href="#">Newest</a></li>
                                <li className="nav-item"><a className="nav-link" href="#">Most Answered</a></li>
                                <li className="nav-item"><a className="nav-link" href="#">Most Voted</a></li>
                                <li className="nav-item"><a className="nav-link" href="#">Answered</a></li>
                                <li className="nav-item"><a className="nav-link" href="#">Unanswered</a></li>
                            </ul>
                        </div>
                        <hr />
                        { loading ? (
                            <div>Loading...</div>
                        ) : error ? (
                            <div>{error}</div>
                        ) : (
                            questions.map((question) => (
                                <QuestionCard
                                    data = {question}
                                    />
                            ))
                        )}
                    </div>

                    <div className="col-lg-2" style={{borderWidth: '-5px'}}>
                        <ul className="list-group text-body float-left">
                            <li className="list-group-item text-body"><i className="fas fa-home" style={{marginRight: '9px'}}></i><span>All Users</span></li>
                            <li className="list-group-item" ><i className="fas fa-tags" style={{marginRight: '9px'}}></i><span>Tag Questions</span></li>
                            <li className="list-group-item" ><i className="fa fa-star" style={{marginRight: '9px'}}></i><span>Voted Questions</span></li>
                        </ul>
                    </div>
                </div>
            </div>
        </section>

    </main>
    );

}

export default MainScreen;
