import React from 'react';
import { useSelector } from 'react-redux';

function Header(props) {

    const userSignin = useSelector((state) => state.userSignin);
    const { userInfo } = userSignin;

    return (
    <nav className="navbar navbar-light navbar-expand-lg fixed-top bg-white clean-navbar">
        <div className="container"><a className="navbar-brand logo" href="#">QuestionByte</a><button data-toggle="collapse" className="navbar-toggler" data-target="#navcol-1"><span className="sr-only">Toggle navigation</span><span className="navbar-toggler-icon"></span></button>
            <div className="collapse navbar-collapse" id="navcol-1">
                <ul className="nav navbar-nav ml-auto">
                    <li className="nav-item"><a className="nav-link active" href="index.html">Home</a></li>
                    <li className="nav-item"><a className="nav-link" href="about-us.html"><strong>ABOUT US</strong></a></li>
                    <li className="nav-item"><a className="nav-link" href="pricing.html">Blog</a></li>
                    <li className="nav-item"><a className="nav-link" href="contact-us.html">Contact Us</a></li>
                    <li className="nav-item">
                    {userInfo ? (
                        <Link className="nav-link active" aria-current="page" to="/profile">{userInfo.name}</Link>
                    ) : (
                        <Link className="nav-link active" aria-current="page" to="/signin">Sign In</Link>
                    )}
                        <a className="nav-link active" data-toggle="modal" href="#login" type="button">Login</a>
                    </li>
                </ul>
            </div>
        </div>  
    </nav>
    );

}

export default Header;
