import React, { useEffect, useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { Link } from 'react-router-dom';
import { signin } from '../actions/userActions';

function Login(props) {

    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const userSignin = useSelector(state => state.userSignin);
    const [message, setMessage] = useState('');
    const { loading, userInfo, error } = userSignin;
    const dispatch = useDispatch();
    const redirect = props.location.search ? props.location.search.split("=")[1] : '/';
    useEffect(() => {
        console.log(userInfo);
        if (userInfo ) {
            if (userInfo.status==='success') {
                props.history.push('/');
            } else {
                setMessage(userInfo.message);
            }
        } 
        return () => {
        //
        };
    }, [userInfo]);

    const submitHandler = (e) => {
        e.preventDefault();
        //console.log(email,password)
        dispatch(signin(email, password));

    }

    return (
    <div class="register-photo" style={{marginTop: '30px',paddingBottom: '20px'}}>
        <div class="form-container">
            <div class="image-holder"></div>
            <form onSubmit={submitHandler}>
                <h2 class="text-center"><strong>Create</strong> an account.</h2>
                <div class="form-group"><input type="email" class="form-control" onChange={(e) => setEmail(e.target.value)} placeholder="Email" /></div>

                <div class="form-group"><input type="password" class="form-control" onChange={(e) => setPassword(e.target.value)}  placeholder="Password" /></div>
                {loading && <div>Loading...</div>}
                {error && <div>{error}</div>}
                <div class="form-group"><button class="btn btn-primary btn-block" type="submit">Sign Up</button></div><Link class="already" to='/'>You already have an account? Login here.</Link></form>
        </div>
    </div>
    );

}

export default Login;
