import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import { useSelector, useDispatch } from 'react-redux';
import { register } from '../actions/questionActions';

function Question(props) {

    const [userName, setUserName] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [passwordConfirm, setPasswordConfirm] = useState('');
    const userRegister = useSelector(state => state.userRegister);
    var { loading, userInfo, error } = userRegister;
    const dispatch = useDispatch();

    const redirect = props.location.search ? props.location.search.split("=")[1] : '/';
    useEffect(() => {

        if (userInfo) {
        props.history.push(redirect);
        } else {
        //userInfo
        }
        return () => {
        //
        };
    }, [userInfo]);

    const submitHandler = (e) => {
        e.preventDefault();
        if(password===passwordConfirm){
        dispatch(register(userName, email, password, passwordConfirm));
        }else{
        error = "Password doesn't match!!";
        }
    }


    return (
    <div>
        <div className="modal fade" role="dialog" tabIndex="-1" id="askQuestion">
            <div className="modal-dialog" role="document">
                <div className="modal-content">
                    <div className="modal-header">
                        <h4>Ask A Question</h4><button type="button" className="close" data-dismiss="modal" aria-label="Close"><span aria-hidden="true">×</span></button></div>
                    <div className="modal-body">
                        <form>
                            <div className="form-group"><small className="form-text text-muted" style={{fontSize: '19px',fontFamily: 'Lora, serif'}}>Question Title*</small><label style={{color: 'rgb(245,30,16)'}}>Be specific and imagine you’re asking a question to another person</label>
                                <input className="form-control form-control-lg" type="text" placeholder="e.g. Is there an R function for finding the index of an element in a vector?" style={{fontSize: '12px', fontFamily: 'Montserrat, sans-serif'}} required/>
                            </div>
                            <div className="form-group"><small className="form-text text-muted" style={{fontSize: '19px', fontFamily: 'Lora, serif'}}>Question Body*</small><label style={{color: 'rgb(245,30,16)'}}>Include all the information someone would need to answer your question</label>
                                <textarea className="form-control form-control-lg"></textarea>
                            </div>
                            <div className="form-group"><small className="form-text text-muted" style={{fontSize: '19px',fontFamily: 'Lora, serif'}}>Question Tags*</small><label style={{color: 'rgb(245,30,16)'}}>Add up to 5 tags to describe what your question is about</label>
                                <input className="form-control" type="text" placeholder="e.g. (vb.net php json" style={{fontSize: '12px', fontFamily: 'Montserrat, sans-serif'}} required/>
                            </div>
                        </form>
                    </div>
                    <div className="modal-footer"><button className="btn btn-light" data-dismiss="modal" type="button">Close</button><button className="btn btn-primary" type="button">Save</button></div>
                </div>
            </div>
        </div>
    </div>
    );

}

export default Question;
