import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import { useSelector, useDispatch } from 'react-redux';
import { register } from '../actions/userActions';

function Register(props) {

  const [userName, setUserName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [passwordConfirm, setPasswordConfirm] = useState('');
  const userRegister = useSelector(state => state.userRegister);
  var { loading, userInfo, error } = userRegister;
  const dispatch = useDispatch();

  const redirect = props.location.search ? props.location.search.split("=")[1] : '/';
  useEffect(() => {
    console.log(userInfo);
    if (userInfo) {
      props.history.push(redirect);
    } else {
      //userInfo
    }
    return () => {
      //
    };
  }, [userInfo]);

  const submitHandler = (e) => {
    e.preventDefault();
    if(password===passwordConfirm){

      dispatch(register(userName, email, password, passwordConfirm));
    }else{
      error = "Password doesn't match!!";
    }
  }

    return (
    <div class="register-photo" style={{marginTop: '30px',paddingBottom: '20px'}}>
        <div class="form-container">
            <div class="image-holder"></div>
            <form onSubmit={submitHandler}>
                <h2 class="text-center"><strong>Create</strong> an account.</h2>
                <div class="form-group"><input type="email" class="form-control" onChange={(e) => setEmail(e.target.value)} placeholder="Email" /></div>
                <div class="form-group"><input type="text" class="form-control" onChange={(e) => setUserName(e.target.value)} placeholder="User Name" /></div>
                <div class="form-group"><input type="password" class="form-control" onChange={(e) => setPassword(e.target.value)}  placeholder="Password" /></div>
                <div class="form-group"><input type="password" class="form-control" onChange={(e) => setPasswordConfirm(e.target.value)} placeholder="Confirm Password" /></div>
                {loading && <div>Loading...</div>}
                {error && <div>{error}</div>}
                <div class="form-group"><button class="btn btn-primary btn-block" type="submit">Sign Up</button></div>
                <Link class="already" to='/login'>You already have an account? Login here.</Link></form>
        </div>
    </div>
    );

}

export default Register;
