import React, { useEffect, useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { saveQuestion} from '../actions/questionActions';

function Question(props) {

    const [title, setTitle] = useState('');
    const [description, setDescription] = useState('');
    const [tags, setTags] = useState('');
    const question = useSelector((state) => state.questionSave);
    const { questions, loading, error } = question;
    const userSignin = useSelector((state) => state.userSignin);
    const { userInfo } = userSignin;
    const dispatch = useDispatch();
    //const redirect = props.location.search ? props.location.search.split("=")[1] : '/';
    useEffect(() => {
        if (userInfo) {
            //props.history.push(redirect);
        } else {
            //userInfo
        }
        return () => {

        };
    }, [userInfo]);

    const submitHandler = (e) => {
        e.preventDefault();
        dispatch(saveQuestion(userInfo._id, userInfo.userName, title, description,tags.split(',')));

    }


    return (
    <div>
        <div className="modal fade" role="dialog" tabIndex="-1" id="askQuestion">
            <div className="modal-dialog" role="document">
                <div className="modal-content">
                    <div className="modal-header">
                        <h4>Ask A Question</h4><button type="button" className="close" data-dismiss="modal" aria-label="Close"><span aria-hidden="true">×</span></button></div>
                    <div className="modal-body">
                        <form onSubmit={submitHandler}>
                            <div className="form-group"><small className="form-text text-muted" style={{fontSize: '19px',fontFamily: 'Lora, serif'}}>Question Title*</small><label style={{color: 'rgb(245,30,16)'}}>Be specific and imagine you’re asking a question to another person</label>
                                <input className="form-control form-control-lg" onChange={(e) => setTitle(e.target.value)} type="text" placeholder="e.g. Is there an R function for finding the index of an element in a vector?" style={{fontSize: '12px', fontFamily: 'Montserrat, sans-serif'}} required/>
                            </div>
                            <div className="form-group"><small className="form-text text-muted" style={{fontSize: '19px', fontFamily: 'Lora, serif'}}>Question Body*</small><label style={{color: 'rgb(245,30,16)'}}>Include all the information someone would need to answer your question</label>
                                <textarea className="form-control form-control-lg" onChange={(e) => setDescription(e.target.value)}></textarea>
                            </div>
                            <div className="form-group"><small className="form-text text-muted" style={{fontSize: '19px',fontFamily: 'Lora, serif'}}>Question Tags*</small><label style={{color: 'rgb(245,30,16)'}}>Add up to 5 tags to describe what your question is about</label>
                                <input className="form-control" onChange={(e) => setTags(e.target.value)} type="text" placeholder="e.g. (vb.net php json" style={{fontSize: '12px', fontFamily: 'Montserrat, sans-serif'}} required/>
                            </div>
                            <div className="form-group">
                                <button className="btn btn-primary btn-block" type="submit">Save</button>
                            </div>
                        </form>
                    </div>
                    <div className="modal-footer"><button className="btn btn-light" data-dismiss="modal" type="button">Close</button></div>
                </div>
            </div>
        </div>
    </div>
    );

}

export default Question;
