import {
    QUESTION_LIST_REQUEST,
    QUESTION_LIST_SUCCESS,
    QUESTION_LIST_FAIL,
    QUESTION_DETAILS_REQUEST,
    QUESTION_DETAILS_SUCCESS,
    QUESTION_DETAILS_FAIL,
    QUESTION_SAVE_REQUEST,
    QUESTION_SAVE_SUCCESS,
    QUESTION_SAVE_FAIL,
    QUESTION_DELETE_SUCCESS,
    QUESTION_DELETE_FAIL,
    QUESTION_DELETE_REQUEST,
    QUESTION_REVIEW_SAVE_REQUEST,
    QUESTION_REVIEW_SAVE_FAIL,
    QUESTION_REVIEW_SAVE_SUCCESS,
  } from '../constants/questionConstants';
  import axios from 'axios';
  import Axios from 'axios';

  const listQuestions = () => async (dispatch) => {
    try {
      dispatch({ type: QUESTION_LIST_REQUEST });
      const { data } = await axios.get('/api/users/allquestions');
      if(data.status==='success'){
        dispatch({ type: QUESTION_LIST_SUCCESS, payload: data.data });
      } else {
        dispatch({ type: QUESTION_LIST_FAIL, payload: data.message });
      }

    } catch (error) {
      dispatch({ type: QUESTION_LIST_FAIL, payload: error.message });
    }
  };

  const saveQuestion = (askedUserId, askedUserName, title, description, tags) => async (dispatch) => {
    dispatch({ type: QUESTION_SAVE_REQUEST, payload: {askedUserId, askedUserName, title, description, tags} });
    try {
      const { data } = await Axios.post('/api/users/askquestion', {askedUserId, askedUserName, title, description, tags});
      if(data.status==='success'){
        dispatch({ type: QUESTION_SAVE_SUCCESS, payload: data.data });
        return "success";
      }else {
        dispatch({ type: QUESTION_SAVE_FAIL, payload: data.message });
        return "success";
      }

    } catch (error) {
      dispatch({ type: QUESTION_SAVE_FAIL, payload: error.message });
    }
  };

  const saveProduct = (question) => async (dispatch, getState) => {
    try {
      dispatch({ type: QUESTION_SAVE_REQUEST, payload: question });
      const {
        userSignin: { userInfo },
      } = getState();
      if (!question._id) {
        const { data } = await Axios.post('/api/products', question, {
          headers: {
            Authorization: 'Bearer ' + userInfo.token,
          },
        });
        dispatch({ type: QUESTION_SAVE_SUCCESS, payload: data });
      } else {
        const { data } = await Axios.put(
          '/api/products/' + question._id,
          question,
          {
            headers: {
              Authorization: 'Bearer ' + userInfo.token,
            },
          }
        );
        dispatch({ type: QUESTION_SAVE_SUCCESS, payload: data });
      }
    } catch (error) {
      dispatch({ type: QUESTION_SAVE_FAIL, payload: error.message });
    }
  };

  const detailsProduct = (productId) => async (dispatch) => {
    try {
      dispatch({ type: QUESTION_DETAILS_REQUEST, payload: productId });
      const { data } = await axios.get('/api/products/' + productId);
      dispatch({ type: QUESTION_DETAILS_SUCCESS, payload: data });
    } catch (error) {
      dispatch({ type: QUESTION_DETAILS_FAIL, payload: error.message });
    }
  };

  const deleteProdcut = (productId) => async (dispatch, getState) => {
    try {
      const {
        userSignin: { userInfo },
      } = getState();
      dispatch({ type: QUESTION_DELETE_REQUEST, payload: productId });
      const { data } = await axios.delete('/api/products/' + productId, {
        headers: {
          Authorization: 'Bearer ' + userInfo.token,
        },
      });
      dispatch({ type: QUESTION_DELETE_SUCCESS, payload: data, success: true });
    } catch (error) {
      dispatch({ type: QUESTION_DELETE_FAIL, payload: error.message });
    }
  };

  const saveProductReview = (productId, review) => async (dispatch, getState) => {
    try {
      const {
        userSignin: {
          userInfo: { token },
        },
      } = getState();
      dispatch({ type: QUESTION_REVIEW_SAVE_REQUEST, payload: review });
      const { data } = await axios.post(
        `/api/products/${productId}/reviews`,
        review,
        {
          headers: {
            Authorization: 'Bearer ' + token,
          },
        }
      );
      dispatch({ type: QUESTION_REVIEW_SAVE_SUCCESS, payload: data });
    } catch (error) {
      // report error
      dispatch({ type: QUESTION_REVIEW_SAVE_FAIL, payload: error.message });
    }
  };

  export {
    listQuestions,
    saveQuestion,
    detailsProduct,
    deleteProdcut,
    saveProductReview,
  };
