import React from 'react';

function AnswerCard(props) {

    return (
    <div class="card" style={{marginTop: '1px',background: 'rgba(255,255,255,0.12)'}}>
        <div class="card-body" style={{background: 'rgba(255,255,255,0)'}}><img src="assets/img/Radhe-Raman-Tiwari.jpg" style={{height: '60px',width: '70px',borderRadius: '40px',borderWidth: '2px',borderStyle: 'solid',marginTop: '22px'}}/>
            <h6 class="text-capitalize text-info card-title" style={{marginLeft: '100px',marginTop: '-75px',width: '420px',fontFamily: 'Architects Daughter, cursive',fontSize: '23px'}}>Radhe Raman Tiwari</h6>
            <h6 style={{marginLeft: '100px',marginTop: '0px',fontSize: '12px',color: '#5bda1f'}}>Added an answer on April 19, 2018 at 2:01 am <br /></h6>
            <p class="text-justify card-text" style={{marginLeft: '100px',marginTop: '9px',textAlign: 'justify',color: '#0b0b0b',fontSize: '13px',fontFamily: 'Lora, serif'}}>His business is advertising a role which interests me. I have suitable  skills for this role but limited experience, so it’s likely to get  better candidates than I – at least on paper. It is reporting to one of  his subordinates, and I have no
                idea how much (if any) input my friend might have into the recruiting process.</p>
        </div>
    </div>

    );

}

export default AnswerCard;
