import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { getAllTags} from '../actions/userActions';
import Spinner from './Spinner';


function TagsScreen(props) {

    const tagList = useSelector((state) => state.userList);
    const { tags, loading, error } = tagList;
    const dispatch = useDispatch();

	useEffect(() => {
        dispatch(getAllTags());
        return () => {
        //
        };
    }, []);

    return (
		<>
		{ loading ? <Spinner/> :
        <div className="row all-user-card">

			{tags.map((tag) => (
            <div className="col-3" key={tag._id}>
				<div className="card">
				  <div className="card-body">
					<h5 className="card-title">{tag.tag}</h5>
					<p className="card-text">{tag.description}</p>
				  </div>
				</div>
			</div>             
            ))
			}


		</div>
		}
		</>
    );


}

export default TagsScreen;
